mapper.update:
update t_role
set Role_name = #{roleName,jdbcType=VARCHAR},
  Role_note = #{roleNote,jdbcType=VARCHAR},
  Role_number = #{roleNumber,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, Role_name, Role_note, Role_number
from t_role
where id = #{id,jdbcType=INTEGER}
